import React from 'react';

function Header() {
    return(
        <h1 className="Header">List Of Top 3 Netflix Series</h1>
    )

}

function Card(props) {
    return (
        <div className="cards">
         <div className="card">
           <img src={props.imgsrc} />
           <div className="class_info">
               <span className="class_category">{props.title}</span>
               <h3 className="card_title">{props.showName}</h3>
               <a href={props.links} target="_blank"> 
               <button>Watch Now</button> 
               </a>
           </div>

         </div>
        </div>
       
    );
    
}

export {
    Card, Header
};
